import React, {useState, useEffect} from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Logado(){
    const Navigate = useNavigate()
    const [usuario, setUsuario] = useState('')

    useEffect(()=>{
        async function verificar(){
            const response = await fetch('http://localhost:5000/inicio', {
                headers:{
                    'Authorization':`${localStorage.getItem('token')}`
                }
            })
            if(response.status === 401){
                Navigate('/')
            }
        }

        verificar()
    })

    // useEffect(()=>{
    //     const token = localStorage.getItem('token')

    //     axios.get('http://localhost:5000/inicio',{
    //         headers:{
    //             'Authorization':`${token}`
    //         }
    //     }).then(response=>{
    //         setUsuario(response.data.usuario)
    //         console.log(usuario.name)
    //     }).catch(error=>{
    //         console.error(error)
    //         Navigate('/')
    //     })
    // }, [Navigate])

    return(
        <div>
            {usuario &&(
            <h1>
                {usuario.name}
            </h1>)
            }
        <div className="logado">
        <div className="hero">
        <img src="./img/background.jpg" alt="Background" className="background-image" />
        <h1 className="welcome-message">Olá, {userName}!</h1>
        <p className="landing-text">Seja bem-vindo(a) ao Clinic Finder. Aqui você encontra informações sobre as principais clínicas de Recife.</p>

        </div>
        </div>
        </div>
    )
}

export default Logado
