import React, { useEffect } from 'react';
import { MapContainer, TileLayer, Marker } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';




function MapPage() {
  useEffect(() => {
    const loadMapScript = () => {
      const apiKey = "YOUR_API_KEY"; // Substitua pela sua chave de API do Google Maps

      const script = document.createElement("script");
      script.src = `https://maps.googleapis.com/maps/api/js?key=${apiKey}`;
      script.defer = true;
      script.async = true;

      script.onload = initializeMap;

      document.body.appendChild(script);
    };

    const initializeMap = () => {
      // Configurações do mapa
      const mapOptions = {
        center: { lat: -23.550520, lng: -46.633308 }, // Coordenadas para o centro do mapa
        zoom: 12, // Zoom inicial do mapa
      };

      // Cria o mapa
      const map = new window.google.maps.Map(document.getElementById("map"), mapOptions);

      // Adicione marcadores, linhas ou outras camadas ao mapa, se necessário

      // Exemplo de adição de marcador
      const marker = new window.google.maps.Marker({
        position: { lat: -23.550520, lng: -46.633308 }, // Coordenadas do marcador
        map: map, // Mapa ao qual o marcador será adicionado
        title: "São Paulo", // Título do marcador
      });
    };

    loadMapScript();
  }, []);

  return <div id="map" style={{ height: "400px" }}></div>;
}

export default MapPage;
